import java.io.*;class BacaFile{	public static void main(String [] a) throws Exception	{		if(a.length==0)		{			System.out.println("Cara Pakai : java BacaFile <namafile>");		}else{			/**			file digunakan untuk mengambil propertis dari suatu file			*/			File f=new File(a[0]);			if(f.exists())			{				/**				jika filenya ada kita inputkan				*FileInputStream digunakan untuk baca file dalam mode bytes				*/								FileInputStream fis=new FileInputStream(f);				int data=0;				/**				*fis digunakan untuk menbaca data				*kalo "-1" maksudnya mengambil sampai data terakhir untuk ditampilkan				*/				while((data=fis.red())!=-1)				{					/**					*kalo kita tidak memasukkan (char) maka yang akan tampil karakter model ASCII yaitu hanya angka yang akan tampil					*untuk menampilkan huruf tinggal tambahkan (char)*/					System.out.println((char)data);				}				}else{					System.out.println("File : "+a[0]+", tidak ada, jangan ngawur!!!");				}			}	}}