public class KalkulatorJadul{	public static void main(String [] d)	{		if(d.length==0)			{			System.out.println("Cara pakai : java KalkulatorJadul bil1 bil2");		}else {			int bil1=Integer.parseInt(d[0]);			int bil2=Integer.parseInt(d[1]);			int hasil=bil1*bil2;						System.out.println("Hasil kali "+bil1+", dengan "+bil2);			System.out.println(hasil);		}			}}// javap java.util.Scanner//untuk cari fungsi fungsi java