public class Mobil{	public int roda=4;	public int mesin=1;	public int body=1;	/*Ini adalah konstruktor default */	public Mobil()	{		this(4,1,1);		System.out.print("Create Object baru....");	}	/** 	ini namanya overloading, constructornya sama(Mobil) tapi parameternya berbeda 	*/	public Mobil(int roda, int mesin, int body)	{		/**		System.out.println("Sebuah object mobil dibuat..."); 		*/		this.roda=roda;		this.mesin=mesin;		this.body=body;	}		public static void  maju()	{		System.out.println("MajuBos..");	}	public void mundur()	{		System.out.println("Mundur Bos..");	}	public void belok()	{		System.out.println("Belok..");	}	public void berhenti()	{		System.out.println("Berhenti..");	}	}