public class MyString{	public static void main(String [] args)	{		String kota="kEnDari";				char pertama=kota.charAt(3);		System.out.println(pertama);				String besar=kota.toUpperCase();		System.out.println(besar);				String kecil=kota.toLowerCase();		System.out.println(kecil);						System.out.println(args[3]);					}}